/**
 * @param {number[][]} image
 * @param {number} sr
 * @param {number} sc
 * @param {number} newColor
 * @return {number[][]}
 */
var floodFill = function(image, sr, sc, newColor) {
    function check4dire(image,sr,sc,oldC,newC){
        if(image[sr][sc]===oldC)image[sr][sc]=newC
        else return
        let rLen=image.length,cLen=image[0].length
        if(sr<rLen-1)check4dire(image,sr+1,sc,oldC,newC)
        if(sr>0)check4dire(image,sr-1,sc,oldC,newC)
        if(sc<cLen-1)check4dire(image,sr,sc+1,oldC,newC)
        if(sc>0)check4dire(image,sr,sc-1,oldC,newC)
    }
    if(image[sr][sc]!==newColor)check4dire(image,sr,sc,image[sr][sc],newColor)
    return image
};
