/**
 * @param {number} N
 * @return {number}
 */
var monotoneIncreasingDigits = function(N) {
    let str=N+''
    let len=str.length
    let bit=0
    let res=+(str[bit]-1+'9'.repeat(len-bit-1))
    while(res<N){
      bit++
      let times=len-bit-1
      let pre=str.substr(0,bit-1),
          midBefore=str.substr(bit-1,bit),
          mid=(+str[bit])-1,
          next='9'.repeat(times>0?times:0)
      if(Number.isNaN(mid))return +pre
      if(+mid<+midBefore){
        if(midBefore-mid>1)break
        continue
      }
      res=+(pre+mid+next)
    }
    return res
};
