/**
 * @param {number[]} nums
 * @return {number}
 */
var deleteAndEarn = function(nums) {
    let count=Array(10002).fill(0)
    for(let i=0;i<nums.length;i++){
        count[nums[i]]+=nums[i]
    }
    for(let i=2;i<count.length;i++){
        count[i]=Math.max(count[i]+count[i-2],count[i-1])
    }

    return count[count.length-1]
};
