/**
 * @param {number[]} nums
 * @return {number}
 */
var dominantIndex = function(nums) {
    let first=-1,second=-1,idx=-1
    for(let i=0;i<nums.length;i++){
      let cur=nums[i]
      if(cur>first){
        second=first
        first=cur
        idx=i
      }else if(cur>second){
        second=cur
      }

    }
  // console.log(first,second)
    if(second===-1 || first/second>=2)return idx
    else return -1
};
