/**
 * @param {string} licensePlate
 * @param {string[]} words
 * @return {string}
 */
var shortestCompletingWord = function(licensePlate, words) {
    let letterArr=licensePlate.toLowerCase().match(/[a-z]/g)
    let min=16,id=-1;
    let cache;
    // let count=0
    for(let i=0;i<words.length;i++){
        cache={}
        let flag=true
        let sec
        for(let j=0;j<letterArr.length;j++){
            // count++
            sec=0
            if(cache[letterArr[j]]!==undefined)sec=cache[letterArr[j]]+1;
            // console.log(letterArr[j],sec)
            let findIndex=words[i].toLowerCase().indexOf(letterArr[j],sec);
            if(findIndex===-1){
                flag=false;
                break;
            }
            cache[letterArr[j]]=findIndex
        }
        // console.log(i,flag,words[i].length<min)
        if(flag && words[i].length<min){
            min=words[i].length
            id=i
        }
    }
    // console.log(min,count)
    return words[id]
};
