/**
 * @param {number[]} nums
 * @return {void} Do not return anything, modify nums in-place instead.
 */
var sortColors = function(nums) {
    let l=0,r=nums.length-1
    let pivot=1
    for(let i=0;i<=r;i++){
      if(nums[i]<pivot) swap(nums,i,l++)
      else if(nums[i]>pivot) swap(nums,i--,r--)
    }
    function swap(arr,i,j){
      let t=arr[i]
      arr[i]=arr[j]
      arr[j]=t
    }
    return nums
};
