/**
 * @param {string[]} deadends
 * @param {string} target
 * @return {number}
 */
var openLock = function(deadends, target) {
    let deadHash=new Set(deadends)
    // for(let i=0;i<deadends.length;i++)deadHash[deadends[i]]=true
    let lock='0000'
    if(deadHash.has(lock))return -1
    let tryed=new Set(),trying=['0000']
    tryed.add(lock)
    // tryed[lock]=lock
    let step=0
    while(trying.length>0){
        let len=trying.length
        for(let i=0;i<len;i++){
            let newLock=trying.shift()
            if(target===newLock)return step
            for(let i=0;i<4;i++){
                let lock1=newLock.substring(0,i)+(newLock[i]==='9'?'0':+newLock[i]+1+'')+newLock.substring(i+1)
                let lock2=newLock.substring(0,i)+(newLock[i]==='0'?'9':+newLock[i]-1+'')+newLock.substring(i+1)
                if(!tryed.has(lock1) && !deadHash.has(lock1)){
                    tryed.add(lock1)
                    trying.push(lock1)
                }
                if(!tryed.has(lock2) && !deadHash.has(lock2)){
                    tryed.add(lock2)
                    trying.push(lock2)
                }            
            }
        }
       step++
    }
    return -1
};
