/**
 * @param {number} n
 * @param {number} k
 * @return {number[][]}
 */
var combine = function(n, k) {
    let result=[],temp=[]
    let nums=[]
    for(let i=0;i<n;i++){
        nums[i]=i+1
    }
    backtrack(result,temp,nums,0)
    return result
    function backtrack(result,temp,nums,start){
        if(temp.length>k)return
        if(temp.length===k)result.push(temp.slice())
        for(let i=start;i<nums.length;i++){
            temp.push(nums[i])
            backtrack(result,temp,nums,i+1)
            temp.pop()
        }
    }
};
