/**
 * @param {number[]} answers
 * @return {number}
 */
var numRabbits = function(answers) {
    let hashMap={}
    // let uniq=[]
    for(let i=0;i<answers.length;i++){
        let cur=answers[i]
        if(hashMap[cur]===undefined){
          hashMap[cur]=1
        }else{
          hashMap[cur]++
        }
    }
    let sum=0
    for(let k in hashMap){
        let num=hashMap[k]
        let value=parseInt(k,10)
        sum+=Math.floor(num/(value+1))*(value+1)
        if(num%(value+1)!==0)sum+=value+1
    }
    return sum
};
