/**
 * @param {number} N
 * @return {number}
 */
var rotatedDigits = function(N) {
    let regUn=/(3|4|7)/
    let regSame=/(0|1|8)/
    function checkN(n){
        let flag=true
        let sN=n+''
        for(let i=0;i<sN.length;i++){
            if(regUn.test(sN[i]))return false
            if(!regSame.test(sN[i]))flag=false
        }
        if(!flag)return true
        else return false
    }
    let count=0
    for(let i=1;i<=N;i++){
        let sI=i+''
        let n=sI[0]
        let d=sI.length-1
        if(regUn.test(n)){
            i=i+Math.pow(10,d)-1
            continue;
        }
        if(checkN(i))count++
    }
    return count;
};
