/**
 * @param {number[][]} ghosts
 * @param {number[]} target
 * @return {boolean}
 */
var escapeGhosts = function(ghosts, target) {
    function getDis(a,b){
        return Math.abs(a[0]-b[0])+Math.abs(a[1]-b[1])
    }
    let myDis=getDis([0,0],target)
    // let ghDis=40000
    for(let i=0;i<ghosts.length;i++){
        let ghDis=getDis(ghosts[i],target)
        if(ghDis<myDis)return false
    }
    return true
};
