/**
 * @param {string} A
 * @param {string} B
 * @return {boolean}
 */
var rotateString = function(A, B) {
  if(A.length!==B.length)return false
  if(A===B)return true
  let len=A.length
  for(let i=0;i<len-1;i++){
    let misMatch=false
    for(let j=0;j<len;j++){
      if(A[j]!==B[(j+i)%len]){
        misMatch=true
        break;
      }
    }
      if(!misMatch)return true
  }
    return false
};
