/**
 * @param {string[]} words
 * @return {number}
 */
var uniqueMorseRepresentations = function(words) {
    let lM=[".-","-...","-.-.","-..",".","..-.","--.","....","..",".---","-.-",".-..","--","-.","---",".--.","--.-",".-.","...","-","..-","...-",".--","-..-","-.--","--.."];
    let lS="abcdefghijklmnopqrstuvwxyz"
    let set=new Set()
    let cache={}
    function f(x){
        if(cache[x]){
            return cache[x]
        }
        return cache[x]=lM[lS.search(x)]
    }
    for(let i=0;i<words.length;i++){
        let s=''
        let a=words[i].split("")
        for(let j=0;j<a.length;j++){
            s+=f(a[j])
        }
        set.add(s)
    }
    return set.size

};
