/**
 * @param {number[]} widths
 * @param {string} S
 * @return {number[]}
 */
var numberOfLines = function(widths, S) {
    let cache={};
    let line=1,sum=0;
    function checkAsi(l){
        if(cache[l]){
            return cache[l]
        }
        // let asi=l.charCodeAt()-97
        return cache[l]=l.charCodeAt()-97;
        // return asi;
    }
    for(let i=0;i<S.length;i++){
        let curCount=widths[checkAsi(S[i])]
        // console.log(sum)
        if(sum+curCount>100){
            line++;
            sum=0;
        }
        sum+=curCount;
    }
    return [line,sum]
};
