/**
 * @param {string} paragraph
 * @param {string[]} banned
 * @return {string}
 */
var mostCommonWord = function(paragraph, banned) {
    let cache={}
    let max=0,maxWord=''
    let parArr=paragraph.split(/[!?',;\.\s]/)
    for(let i=0;i<parArr.length;i++){
        let cur=parArr[i].toLowerCase()
        if(!cur)continue
        if(isBanned(banned,cur))continue;
        if(!cache[cur])cache[cur]=0
        cache[cur]++
        if(cache[cur]>max){
            max=cache[cur]
            maxWord=cur
        }
    }
    function isBanned(banned,n){
        for(let i=0;i<banned.length;i++){
            if(n===banned[i])

                return true
        }
        return false
    }

    return maxWord
};
