/**
 * @param {string} S
 * @return {string}
 */
var toGoatLatin = function(S) {
    function repeatA(n){
        let s='';
        for(let i=0;i<n;i++){
            s+="a"
        }
        return s
    }
    let wordArr=S.split(' ');
    for(let i=0;i<wordArr.length;i++){
        if(wordArr[i].match(/^(a|e|i|o|u)/i)){
            wordArr[i]+="ma"
        }else{
            wordArr[i]=wordArr[i].replace(/^(.)(.*)/,'$2$1ma')
        }
        wordArr[i]+=repeatA(i+1)
    }
    return wordArr.join(" ")
};
