/**
 * Definition for singly-linked list.
 * function ListNode(val) {
 *     this.val = val;
 *     this.next = null;
 * }
 */
/**
 * @param {ListNode} head
 * @return {ListNode}
 */
var deleteDuplicates = function(head) {
    if(!head)return head
    let flag=head.val
    let node=head
    while(node && node.next){
      if(node.next.val===flag){
        node.next=node.next.next
      }else{
        flag=node.next.val
        node=node.next
      }
    }
    return head
};
