/**
 * @param {string} S
 * @return {number[][]}
 */
var largeGroupPositions = function(S) {
    let start=0,res=[]
    for(let i=0;i<S.length;i++){
        if(S[i]===S[start])continue
        else{
            if(i-start>2)res.push([start,i-1])
            start=i
        }
    }
    if(S.length-start>2)res.push([start,S.length-1])
    return res
};
