/**
 * @param {number[][]} A
 * @param {number[][]} B
 * @return {number}
 */
var largestOverlap = function(A, B) {
    let N = A.length;
    let counts = []
    for(let i=0;i<2*N;i++){
        counts[i]=[]
        for(let j=0;j<2*N;j++){
            counts[i][j]=0
        }
    }
    let max = 0;
    for(let i=0; i<N;++i){
      for(let j=0; j<N;++j){
        if(A[i][j]==1){
          for(let i2=0; i2<N;++i2){
            for(let j2=0; j2<N;++j2){
              if(B[i2][j2]==1){
                counts[N+i2-i][N+j2-j]++;
                max=Math.max(max,counts[N+i2-i][N+j2-j]);
              }
            }
          }
        }
      }
    }
    return max;
};
