/**
 * @param {number[][]} rooms
 * @return {boolean}
 */
var canVisitAllRooms = function(rooms) {
    let cache={}
    cache[0]=rooms[0]
    function checkKeys(n){
        let room=rooms[n]
        for(let i=0;i<room.length;i++){
            if(!cache[room[i]]){
                cache[room[i]]=rooms[room[i]]
                checkKeys(room[i])
            }
        }
    }
    checkKeys(0)
    return Object.keys(cache).length===rooms.length

};
