/**
 * @param {string} S
 * @return {number}
 */
var scoreOfParentheses = function(S) {
    let res
    function resolveP(s){
        // console.log(s)
        if(s.length===0){return 0}
        let count=0
        if(s[0]==="(" && s[1]===")"){
           return 1+(resolveP(s.substring(2)))
        }else{
            for(let i=0;i<s.length;i++){
                if(s[i]==="(")count++
                if(s[i]===")")count--
                if(count===0){
                    return 2*(resolveP(s.substring(1,i)))+resolveP(s.substring(i+1))
                }
            }
        }

    }
    res=resolveP(S)
    return res
};
