/**
 * @param {string} A
 * @param {string} B
 * @return {boolean}
 */
var buddyStrings = function(A, B) {

    let count=0;
    let a=[],b=[]
    for(let i=0;i<A.length;i++){
        if(A[i]!==B[i]){
            count++;
            a.push(A[i])
            b.push(B[i])
        }
    }
    if(count>2){
        return false
    }else{
        if(count===2){
            if(a.sort().join('')===b.sort().join('')){
                return true
            }
            return false
        }
        if(count<2){
            return new Set(A).size!==A.length
        }
    }
};
