/**
 * @param {number[]} bills
 * @return {boolean}
 */
var lemonadeChange = function(bills) {
    let cache={
        5:0,
        10:0,
        20:0
    }
    for(let i=0;i<bills.length;i++){
        if(bills[i]===10){
            if(cache[5]<1){
                // console.log(cache,i)
                return false
            }
            cache[5]-=1
            cache[10]+=1
        }else if(bills[i]===20){
            if(cache[5]<3 && (cache[10]<1 || cache[5]<1)){
                // console.log(cache,i)
                return false
            }else if(cache[10]>=1 && cache[5]>=1){
                cache[5]-=1;
                cache[10]-=1;
                cache[20]+=1
            }else if(cache[5]>=3){
                cache[5]=cache[5]-3
                cache[20]+=1
            }
        }else if(bills[i]===5){
            cache[5]+=1
        }
    }
    return true
}
