/**
 * @param {number[][]} A
 * @return {number[][]}
 */
var transpose = function(A) {
    let inLen=A[0].length;
    let outLen=A.length;
    let arr=[]
    for(let i=0;i<inLen;i++){
        for(let j=0;j<outLen;j++){
            if(!arr[i]){
                arr[i]=[]
            }
            arr[i][j]=A[j][i]
        }
    }
    return arr
};
