/**
 * @param {number} N
 * @return {number}
 */
var binaryGap = function(N) {
    let last
    let max=0
    let count=0
    while(N>=1){
        let n=N % 2
        count++;
        if(n===1){
            if(last!==undefined && count-last>max){
                max=count-last
            }
            last=count;
        }
        N=parseInt(N/2,10)
    }
    return max
};
