/**
 * @param {number[]} people
 * @param {number} limit
 * @return {number}
 */
var numRescueBoats = function(people, limit) {
    people.sort((a,b)=>a-b)
    let l=0,r=people.length-1
    let count=0
    while(l<r){
      let min=people[l]
      let max=people[r]
      if(max+min<=limit){
        count++
        l++
        r--
      }else{
        r--
        count++
      }
    }
    if(l===r)return count+1
    else return count
};
