/**
 * @param {string} A
 * @param {string} B
 * @return {string[]}
 */
var uncommonFromSentences = function(A, B) {
    let sArr=(A+" "+B).split(" ")
    let cache={}
    let res=[]
    // console.log(A+" "+B)
    for(let i=0;i<sArr.length;i++){
        let cur=sArr[i]
        if(!cache[cur])cache[cur]=0
        cache[cur]++
    }
    for(let k in cache){
        if(cache[k]===1)
        res.push(k)
    }
    return res
};
