/**
 * @param {number} N
 * @param {number[][]} dislikes
 * @return {boolean}
 */
var possibleBipartition = function(N, dislikes) {
  let graph=[]
  for(let i=1;i<=N;i++){
    graph[i]=[]
  }
  for(let i=0;i<dislikes.length;i++){
    let v=dislikes[i][0],
        w=dislikes[i][1]
    graph[v].push(w)
    graph[w].push(v)
  }
  let count=0
  let color=[],marked=[]
  function dfs(v){
    marked[v]=true
    let adj=graph[v]
    for(let i=0;i<adj.length;i++){
      if(marked[adj[i]]==null){
        color[adj[i]]=!color[v]
        dfs(adj[i])
      }else if(!!color[adj[i]]===!!color[v])return false
    }
    return true
  }
  for(let i=1;i<graph.length;i++){
    if(marked[i])continue
    if(!dfs(i))return false
  }
  return true
};
