/**
 * @param {number[]} A
 * @param {number[]} B
 * @return {number[]}
 */
var fairCandySwap = function(A, B) {
    let sumA=0,sumB=0,res=[]
    for(let i=0;i<A.length;i++){
        sumA+=A[i]
    }
    for(let i=0;i<B.length;i++){
        sumB+=B[i]
    }
    let gap=(sumA-sumB)/2
    for(let i=0;i<A.length;i++){
        let iB=B.indexOf(A[i]-gap)
        if(iB!==-1){
            res.push(A[i],B[iB])
            return res
        }
    }
    return res
};
