    function compute(n){
        if(n===1)return [0,1]
        let arr=compute(n-1)
        let newArr=[]
        for(let i=0;i<arr.length;i++){
            newArr.push("0"+arr[i])
        }
        for(let i=arr.length-1;i>=0;i--){
            newArr.push("1"+arr[i])
        }
        return newArr
    }
    if(n===0)return [0]
    return compute(n).map(n=>parseInt(n,2))
};
