/**
 * @param {string[]} words
 * @param {string} pattern
 * @return {string[]}
 */
var findAndReplacePattern = function(words, pattern) {
    let  c={},d={},res=[],flag=true
    for(let i=0;i<words.length;i++){
        c={},flag=true,d={}
        for(let j=0;j<words[i].length;j++){
            // console.log(c)
            let cur=words[i][j]
            let curP=pattern[j]
            if(!c[curP])c[curP]=cur
            else{
                if(c[curP]!==cur){
                    flag=false
                    break
                }
            }
            if(!d[cur])d[cur]=curP
            else{
                if(d[cur]!==curP){
                    flag=false
                    break
                }
            }            

        }
        if(flag)
        res.push(words[i])
    }
    return res
};
