/**
 * @param {number[]} A
 * @return {boolean}
 */
var isMonotonic = function(A) {
    let last=A[1]-A[0]
    for(let i=2;i<A.length;i++){
        if(last===0){
            last=A[i]-A[i-1]
            continue
        }
        if((A[i]-A[i-1])/last<0){
            return false
        }
    }
    return true
};
