/**
 * @param {number[]} A
 * @param {number} K
 * @return {number}
 */
var smallestRangeI = function(A, K) {
    let min=Infinity,max=-Infinity
    for(let i=0;i<A.length;i++){
        let cur=A[i]
        if(cur>max)max=cur
        if(cur<min)min=cur
    }
    let gap=max-K-(min+K)
    return gap<0?0:gap
};
