/**
 * @param {number[]} deck
 * @return {boolean}
 */
var hasGroupsSizeX = function(deck) {
    if(deck.length<2)return false
    let map={}
    for(let i=0;i<deck.length;i++){
        if(!map[deck[i]])map[deck[i]]=1
        else map[deck[i]]++
    }
    // console.log(map)
    let values=Object.values(map)
    if(values.length===1)return true
    let minC=getMinC(values[0],values[1])
    if(minC===1)return false
    function getMinC(a,b){
        let mod=a%b
        while(mod!==0){
            a=b
            b=mod
            mod=a%b
        }
        return b
    }
    for(let i=0;i<values.length;i++){
        if(values[i]<2)return false
        if(values[i]%minC!==0){
            let newMinC=getMinC(values[i],minC)
            if(newMinC===1)return false
            else minC=newMinC
        }
    }
    return true
};
