/**
 * @param {string} name
 * @param {string} typed
 * @return {boolean}
 */
var isLongPressedName = function(name, typed) {
    let i=0,j=0
    if(name.length>typed.length)return false
    for(let i=0;i<typed.length;i++){
        if(j<name.length && typed[i]===name[j]){
            j++;continue
        }
        else if(typed[i-1]===typed[i]){
            continue;
        }
        else return false
    }
    if(j===name.length-1)return false
    return true
};
