/**
 * @param {string} S
 * @return {number}
 */
var minFlipsMonoIncr = function(S) {
    let s0=0,s1=0,prev_s0,prev_s1
    for(let i=0;i<S.length;i++){
        prev_s0=s0;prev_s1=s1
        s0=prev_s0+(S[i]==="0"?0:1)
        s1=Math.min(prev_s0,prev_s1)+(S[i]==="1"?0:1)
    }
    return Math.min(s0,s1)
};
