/**
 * @param {number[]} A
 * @return {number}
 */
var minIncrementForUnique = function(A) {
  // A.sort((a,b)=>a-b)
  let repeat={}  
  for(let i=0;i<A.length;i++){
   if(repeat[A[i]]==null)repeat[A[i]]=0
    else repeat[A[i]]++
  }
  let res=0
  // console.log(repeat)
  for(let i=0;i<A.length;i++){
    if(repeat[A[i]]>0){
      let step=A[i]
      let c=0
      while(repeat[step]!=null){
        c++
        step++
      }
      // console.log(c)
      repeat[step]=0
      repeat[A[i]]--
      res+=c
    }
  }
  return res
};
