/**
 * @param {number[]} A
 * @return {string}
 */
var largestTimeFromDigits = function(A) {
  A.sort((a,b)=>a-b)
  let temp=[],used=[],maxRes=null
  function bt(temp,A,used){
    if(temp.length===4){
      // console.log(temp)
      if(isValid(temp))maxRes=temp.slice()
    }
    for(let i=0;i<A.length;i++){
      if(A[i]>=3 && temp.length===0)break 
      if(used[i])continue
      temp.push(A[i])
      used[i]=true
      bt(temp,A,used)
      temp.pop()
      used[i]=false
    }
  }
  bt(temp,A,used)
  function isValid(arr){
    if(arr[0]===0 && arr[2]<=5){
      return true
    }
    if(arr[0]===1 && arr[2]<=5){
      return true
    }
    if(arr[0]===2 && arr[1]<=3 && arr[2]<=5){
      return true
    }
    return false
  }
  if(!maxRes)return ''
  return ''+maxRes[0]+maxRes[1]+":"+maxRes[2]+maxRes[3]
};
