/**
 * @param {number[]} A
 * @return {boolean}
 */
var canReorderDoubled = function(A) {
  A.sort((a,b)=>a-b)
  let group1={},group2={},len1=0,len2=0,special=0
  for(let i=0;i<A.length;i++){
    let cur=A[i]
    if(cur===0){
      special++
      continue
    }
    let tar1=cur>0 ?cur/2:cur*2
     if(group1[tar1]>0){
   group1[tar1]--
      len1--
    }else{
      if(group1[cur]==null)group1[cur]=1
      else group1[cur]++
      len1++
    }
  }
  // console.log(len1,special)
  // console.log(group1,group2)
  return len1===0 && special%2===0
};
