(window.webpackJsonp=window.webpackJsonp||[]).push([[38],{1062:function(n){n.exports={content:'<p>\u653b\u51fb\u8005\u9700\u8981\u77e5\u9053\u7684\uff1a</p>\n<ul>\n<li>\u77e5\u9053\u88ab\u653b\u51fb\u7f51\u7ad9\u5730\u5740</li>\n<li>\u77e5\u9053\u88ab\u653b\u51fb\u7f51\u7ad9<strong>\u5185\u90e8</strong>\u7f3a\u5c11\u8ba4\u8bc1\u73af\u8282</li>\n<li>\u77e5\u9053\u9700\u8981\u8bf7\u6c42\u7684(\u76ee\u6807)\u5b58\u653e\u5728\u54ea\u4e2a\u4f4d\u7f6e</li>\n</ul>\n<p>\u88ab\u653b\u51fb\u8fd9\u9700\u8981\u505a\u7684\uff1a</p>\n<ul>\n<li>\u901a\u8fc7\u8bf1\u5bfc\u94fe\u63a5\u8bbf\u95ee\u4e86\u6076\u610f\u7f51\u7ad9</li>\n</ul>\n<p>\u6267\u884c\u5173\u952e\u70b9</p>\n<ul>\n<li>\u6076\u610f\u7f51\u7ad9\u7b2c\u4e00\u6b21\u54cd\u5e94DNS\u4e3a\u5b9e\u9645DNS\uff0cTTL\u6781\u77ed\uff0c\u7136\u540e\u6267\u884c\u8bf7\u6c42(\u4f5c\u7528\u5728\u6076\u610f\u7f51\u7ad9\u4e0a)\u3002</li>\n<li>\u6076\u610f\u7f51\u7ad9\u7b2c\u4e8c\u6b21\u54cd\u5e94DNS\u4e3aTarget\u7684IP\u5730\u5740\uff0c\u7ee7\u7eed\u6267\u884c\u4ee5\u4e0a\u8bf7\u6c42(\u4f5c\u7528\u5728\u76ee\u6807\u7f51\u7ad9\u4e0a)\u3002</li>\n</ul>\n<p>\u9632\u62a4\u63aa\u65bd</p>\n<ol>\n<li>\n<p>\u589e\u52a0\u9a8c\u8bc1(Authorization)\u3002</p>\n</li>\n<li>\n<p>IP\u6839\u636e\u7b2c\u4e00\u6b21DNS\u54cd\u5e94\u503c\u9501\u5b9a\u3002(\u5bf9\u4e8e\u6b63\u5e38\u7684\u52a8\u6001DNS\u4e0d\u65b9\u4fbf)</p>\n</li>\n<li>\n<p>\u6839\u636e\u4e3b\u673a\u5934\u6765\u8fc7\u6ee4\u3002</p>\n<blockquote>\n<p>\u6b64\u6807\u5934\u5305\u542b\u5728\u4ece\u73b0\u4ee3\u6d4f\u89c8\u5668\u53d1\u9001\u7684\u6240\u6709HTTP\u8bf7\u6c42\u4e2d\uff0c\u5b83\u5305\u542b\u671f\u671b\u4e0e\u4e4b\u901a\u4fe1\u7684\u670d\u52a1\u5668\u7684\u4e3b\u673a\uff08\u4e3b\u673a\u540d\uff1a\u7aef\u53e3\uff09\u3002\n\u6b64\u503c\u53ef\u4ee5\u662f\u57df\u540d\u6216IP\u5730\u5740\uff0c\u4f46\u65e0\u8bba\u54ea\u79cd\u65b9\u5f0f\uff0c\u63a5\u6536\u8bf7\u6c42\u7684\u670d\u52a1\u5668\u90fd\u5e94\u9a8c\u8bc1\u5176\u81ea\u5df1\u7684\u4e3b\u673a\u662f\u5426\u4e0e\u6240\u8bf7\u6c42\u7684\u4e3b\u673a\u5339\u914d\u3002\u7531\u4e8eDNS\u91cd\u65b0\u7ed1\u5b9a\u4f9d\u8d56\u4e8e\u4e0e\u57df\u540d\u5173\u8054\u7684\u57fa\u7840IP\u5730\u5740\u7684\u66f4\u6539\uff0c\u91cd\u7ed1\u5b9a\u5230\u76ee\u6807\u670d\u52a1\u7684\u6076\u610fHTTP\u8bf7\u6c42\u4e2d\u7684Host\u5934\u4f1a\u4fdd\u6301\u539f\u59cb\u7684\u57df\u540d\u3002</p>\n</blockquote>\n</li>\n</ol>\n<p>\u53c2\u8003\uff1a<a href="https://bbs.pediy.com/thread-230047.htm">\u5229\u7528DNS\u91cd\u7ed1\u5b9a\u653b\u51fb\u4e13\u7528\u7f51\u7edc</a></p>\n'}}}]);
