(window.webpackJsonp=window.webpackJsonp||[]).push([[515],{1105:function(e){e.exports={content:'<p>\u5173\u4e8e\u4e8c\u53c9\u6811\u7684\u904d\u5386\uff0c\u4f7f\u7528\u6808\u9012\u5f52\u6216\u8005\u4eff\u6808\u5faa\u73af\u90fd\u662f\u9700\u8981<code>O(N)</code>\u7684\u7a7a\u95f4\uff0c<code>Morris Traversal</code>\u4fdd\u8bc1\u4e86\u7a7a\u95f4\u4e3a<code>O(1)</code>\uff0c\u65f6\u95f4\u8fd8\u662f<code>O(N)</code>\uff08\u6bd4\u539f\u6765\u591a\u4e86\u4e00\u904d\uff09\u3002</p>\n<p>\u8fd9\u91cc\u53ea\u4ecb\u7ecd<code>inOrder</code>\u987a\u5e8f\u3002</p>\n<p>\u601d\u8def\uff1a</p>\n<p>\u5bf9\u6bcf\u4e00\u4e2a<code>cur</code>\u8282\u70b9\uff0c\u4f18\u5148\u627e\u5230\u4e00\u4e2a<code>pre</code>\u8282\u70b9\uff0c\u8fd9\u4e2a<code>pre</code>\u8282\u70b9\u7684\u4f5c\u7528\u662f\uff0c\u5f53\u540e\u7eed<code>cur</code>\u8282\u70b9\u904d\u5386                \u5230\u8fd9\u4e2a\u4f4d\u7f6e\u65f6\uff0c\u53ef\u4ee5\u76f4\u63a5\u901a\u8fc7\u8fd9\u4e2a<code>pre</code>\u8282\u70b9\u8fd4\u56de\u5b83\u9700\u8981\u8fd4\u56de\u7684\u4f4d\u7f6e\u3002</p>\n<p>\u4f8b\u5982\uff1a</p>\n<pre class="hljs"><code>        6\n       / \\\n      4   8\n    /  \\\n   2    5\n</code></pre>\n<ul>\n<li>\u4e0a\u9762\u5f53<code>cur</code>\u8282\u70b9\u5728<code>6</code>\u7684\u65f6\u5019\uff0c<code>pre</code>\u8282\u70b9\u4f1a\u5728<code>5</code>\uff0c\u56e0\u4e3a\u540e\u9762\u5f53<code>cur</code>\u8282\u70b9\u904d\u5386\u5230<code>5</code>\u7684\u65f6\u5019\uff0c\u53ef\u4ee5\u901a\u8fc7<code>pre</code>\u8282\u70b9\u76f4\u63a5\u8fd4\u56de<code>6</code></li>\n<li>\u5f53<code>cur</code>\u8282\u70b9\u518d<code>4</code>\u7684\u65f6\u5019\uff0c<code>pre</code>\u8282\u70b9\u4f1a\u5728<code>2</code>\uff0c\u5f53\u540e\u9762<code>cur</code>\u5230<code>2</code>\u7684\u65f6\u5019\uff0c\u53ef\u4ee5\u76f4\u63a5\u8fd4\u56de<code>4</code></li>\n</ul>\n<hr>\n<p><code>pre</code>\u627e\u5230\u4e86\uff0c\u662f\u901a\u8fc7\u4ec0\u4e48\u8fd4\u56de\u5462\uff0c\u56e0\u4e3a\u4e0d\u80fd\u4fee\u6539\u4e8c\u53c9\u6811\u7ed3\u6784\uff0c\u4e5f\u4e0d\u80fd\u4f7f\u7528\u5806\u6808\u8bb0\u5f55\u3002</p>\n<p>\u901a\u8fc7<code>mirror</code>(\u955c\u50cf)\uff0c\u4e5f\u5c31\u662f\u8bf4\uff0c\u5f53\u627e\u5230<code>pre</code>\u7684\u65f6\u5019\uff08\u6bcf\u4e2a<code>pre</code>\u7684\u53f3\u8282\u70b9\u786e\u4fdd\u4e3anull\uff09\uff0c\u5728\u5b83\u7684\u53f3\u8282\u70b9\u521b\u5efa\u4e00\u4e2a\u955c\u50cf\u8282\u70b9\uff0c\n\u8fd9\u4e2a\u955c\u50cf\u8282\u70b9\u76f4\u63a5\u6307\u5411\u5f53\u524d\u7684<code>cur</code>\u8282\u70b9\u3002</p>\n<p>\u8fd9\u4e2a\u64cd\u4f5c\u662f\u4e0d\u5360\u7528\u7a7a\u95f4\u7684\uff0c\u56e0\u4e3a\u53ea\u662f\u4e92\u76f8\u5f15\u7528\u3002</p>\n<p>\u4f8b\u5982\uff1a\u5f53\u4e0a\u9762\u7684<code>cur</code>\u4e3a<code>6</code>\uff0c<code>pre</code>\u4e3a<code>5</code>\uff0c\u90a3\u4e48\u8bbe\u7f6e<code>pre.right=cur</code>\uff0c\u611f\u89c9\u4e0a\u662f\u8fd9\u6837\uff1a</p>\n<pre class="hljs"><code>        6\n       / \\\n      4   8\n    /  \\\n   2    5\n         \\\n          6\n         / \\\n        4   8\n        ...\n</code></pre>\n<p>\u5176\u5b9e\u5e76\u6ca1\u6709\u591a\u51fa\u6765\u90a3\u4e00\u5757\uff0c\u53ea\u662f<code>5</code>\u5f15\u7528\u5230<code>6</code>\u7f62\u4e86</p>\n<pre class="hljs"><code>         6\n       / \u2191 \\\n      4  \u2191  8\n    /   \\\u2191\n  2      5\n  \n</code></pre>\n<hr>\n<p>\u7406\u89e3\u4e86\u8fd9\u4e9b\uff0c\u90a3\u4e48\u540e\u7eed\u5c31\u7b80\u5355\u4e86\uff0c\u5f53<code>cur</code>\u904d\u5386\u5230<code>pre</code>\u7684\u65f6\u5019\u5e76\u4e14\u6253\u5370\u540e\uff0c\u5c06<code>pre</code>\u65b0\u589e\u7684\u5f15\u7528\u5220\u9664\u6062\u590d\u539f\u6765\u7684\u6811\u4fbf\u53ef\u3002</p>\n<p>\u4ee3\u7801\uff1a</p>\n<pre class="hljs"><code><span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">morrisTraversal</span>(<span class="hljs-params">root</span>)</span>{\n  <span class="hljs-keyword">let</span> cur=root,pre\n  <span class="hljs-keyword">while</span>(cur!=<span class="hljs-literal">null</span>){\n    <span class="hljs-comment">// \u5f53\u5de6\u4e3a\u7a7a\uff0c\u76f4\u63a5\u6253\u5370</span>\n    <span class="hljs-keyword">if</span>(cur.left==<span class="hljs-literal">null</span>){\n      <span class="hljs-built_in">console</span>.log(cur.val)\n      cur=cur.right\n    }<span class="hljs-keyword">else</span>{\n      <span class="hljs-comment">// \u5f53\u5de6\u4e0d\u4e3a\u7a7a\uff0c\u5148\u53bb\u627e pre</span>\n      pre=cur.left\n      <span class="hljs-keyword">while</span>(pre.right!=<span class="hljs-literal">null</span> &amp;&amp; pre.right!==cur){\n        pre=pre.right\n      }\n      <span class="hljs-comment">// \u5efa\u7acb\u5f15\u7528\uff0c\u7528\u4e8e\u8fd4\u56de</span>\n      <span class="hljs-keyword">if</span>(pre.right==<span class="hljs-literal">null</span>){\n        pre.right=cur\n        cur=cur.left\n      }<span class="hljs-keyword">else</span>{\n        <span class="hljs-comment">// \u5220\u9664\u5f15\u7528</span>\n        <span class="hljs-built_in">console</span>.log(cur.val)\n        pre.right=<span class="hljs-literal">null</span>\n        cur=cur.right\n      }\n    }\n  }\n}\n</code></pre>\n'}}}]);
