(window.webpackJsonp=window.webpackJsonp||[]).push([[791],{1129:function(n){n.exports={content:'<h4>ReactTransitionGroup</h4>\n<h4>Transition</h4>\n<p>\u9ed8\u8ba4\u5c55\u793a\u7ec4\u4ef6\u67d0\u4e2a\u7279\u5b9a\u72b6\u6001\u7684\u6837\u5f0f\uff0c\u800c\u4e0d\u662f\u521b\u5efa\u6e10\u53d8\u52a8\u753b</p>\n<p>\u4f8b\u5982\uff1a</p>\n<pre class="hljs"><code>&lt;Transition\n        <span class="hljs-keyword">in</span>={toggleShow}\n        timeout={<span class="hljs-number">1000</span>}\n        unmountOnExit\n        &gt;\n    {state=&gt;(\n        &lt;div&gt;{state}&lt;/div&gt;\n    )}\n&lt;<span class="hljs-regexp">/Transition&gt;\n</span></code></pre>\n<p>\u6ce8\u610f\uff1a\u5b50\u7ec4\u4ef6\u662f\u51fd\u6570\uff08\u89c1\u4e0b\u65b9children\uff09</p>\n<h2>CSSTransition</h2>\n<p>\u5c55\u793a\u7ec4\u4ef6\u4ece\u72b6\u6001\u5230\u53e6\u4e00\u4e2a\u72b6\u6001\u7684\u52a8\u6001\u53d8\u5316\uff0c\u9700\u8981\u5b9a\u4e49className\u548c\u76f8\u5173\u6837\u5f0f</p>\n<p>\u4f8b\u5982\uff1a</p>\n<pre class="hljs"><code>&lt;CSSTransition \n        <span class="hljs-keyword">in</span>={toggleShow}\n        className=<span class="hljs-string">"fade"</span>\n        timeout={<span class="hljs-number">300</span>}\n        &gt;\n    <span class="hljs-comment">//\u5b50\u7ec4\u4ef6\uff08\u9700\u8981\u52a8\u753b\u7684\u7ec4\u4ef6\uff09</span>\n&lt;<span class="hljs-regexp">/CSSTransition&gt;\n</span></code></pre>\n<p>css\u4e2d\u8bbe\u7f6e\u5982\u4e0b\uff1a</p>\n<pre class="hljs"><code>.fade-enter {\n  opacity: 0.01;\n}\n.fade-enter-active {\n  opacity: 1;\n  transition: opacity 300ms;\n}\n.fade-exit {\n  opacity: 1;\n}\n.fade-exit-active {\n  opacity: 0.01;\n  transition: opacity 300ms;\n}\n</code></pre>\n<h4>TransitionGroup</h4>\n<p>\u7ba1\u7406\u4e00\u4e9b\u5217\u7ec4\u4ef6\u7684\u52a8\u753b\uff0c\u4f8b\u5982\u5217\u8868</p>\n<pre class="hljs"><code>&lt;ul&gt;\n  &lt;TransitionGroup&gt;\n    {items.map(({ id, text }) =&gt; (\n      &lt;CSSTransition\n        key={id}\n        timeout={500}\n        classNames="fade"\n      &gt;\n        &lt;li&gt;{text}&lt;/li&gt;\n      &lt;/CSSTransition&gt;\n    ))}\n  &lt;/TransitionGroup&gt;\n&lt;<span class="hljs-regexp">/ul&gt;\n</span></code></pre>\n<h4>\u5e38\u7528\u5c5e\u6027</h4>\n<ul>\n<li>children</li>\n</ul>\n<p>\u5f53children\u662f\u4e00\u4e2a\u51fd\u6570\u65f6\uff0c\u63a5\u53d7\u4e00\u4e2a\u53c2\u6570state\uff0cstate\u5305\u542b\u4e86\u5185\u90e8\u7ec4\u4ef6\u7684transition\u72b6\u6001\n<code>\'entering\', \'entered\', \'exiting\', \'exited\', \'unmounted\'</code></p>\n<ul>\n<li>in</li>\n</ul>\n<p>\u5b9a\u4e49\u5f53\u524d\u7ec4\u4ef6\u7684\u72b6\u6001(true\u4e3aentered\uff0cfalse\u4e3aexited)</p>\n<ul>\n<li>unmountOnExit</li>\n</ul>\n<p>\u5f53\u72b6\u6001\u4e3aexited\u7684\u65f6\u5019\u662f\u5426\u9700\u8981unmount</p>\n<ul>\n<li>timeout</li>\n</ul>\n<p>\u5b9a\u4e49transition\u95f4\u9694\uff0c\u53ef\u4ee5\u8fd9\u4e48\u5199\uff1a</p>\n<pre class="hljs"><code>timeout={{\n  <span class="hljs-attr">enter</span>:<span class="hljs-number">300</span>,\n  <span class="hljs-attr">exit</span>:<span class="hljs-number">300</span>\n}}\n</code></pre>\n'}}}]);
